package week1.day2;

public class NumberConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number = -67;
if(number<0){
	number = number*-1;
	System.out.println("The negative number -67 is converted to  " + number);

}
else
	System.out.println(number);
	}

}
